import React from "react";
import NavBar from "./NavBar";
import { getAllDogs,getDogByName } from "../actions/actions";
import { connect } from "react-redux";
import DogCard from "./DogCard";

export class Home extends React.Component {
  
  constructor(props){
      super(props);
      
  }
  

   handleChange = (e) =>{
        e.preventDefault();

        this.props.getAllDogs(e.target.value)

        
        
    }
  //componentdidmount para que se guarden todos lo perros en el state global
  componentDidMount() {
    this.props.getAllDogs();
  }
  componentDidUpdate(){
    this.handleChange(e)
  }
  //le saque el let
 

  render() {
    return (
      <React.Fragment>
        
        <form className="filter">
          <h3>Filter by:</h3>
        <label>Name</label>
        <input type="text" name="name" 
        onChange={(e)=> this.handleChange(e)} />
        <label>Temperament/s</label>
        <input type="text" name="temperament" />
        
        <select name="" id="alfabetic" placeholder="Ascendant/Descentent">
    
          <option value="ascendant">Ascendant</option>
          <option value="descentent">descendent</option>
        </select>
        </form>
        <div className="dogs-container">
          
          {this.props.dogs &&
            this.props.dogs.map((dog) => {
              //console me dice que cada child teiene q tener una key para ya la tiene nose q onda

              return (
                <DogCard
                  key={dog.id}
                  id={dog.id}
                  name={dog.name}
                  temperament={dog.temperament}
                  weight={dog.weight.imperial}
                  
                  image={dog.image?.url}
                />
              );
            })}
        </div>
      </React.Fragment>
    );
  }
}

export function mapStateToProps(state) {
  return { dogs: state.dogs };
}

export const mapDispatchToProps = { getAllDogs };

export default connect(mapStateToProps, mapDispatchToProps)(Home);
