import {
  GET_ALL_DOGS,
  GET_DOG_BY_ID,
  CREATE_DOG,
  GET_DOG_BY_NAME,
  GET_TEMPERAMENTS,
  FILTER_BY_TEMPERS,
  GET_ORDERED,
} from "../actions/actions";
const initialState = {
  dogs: [],
  dogDetail: {},
  temperaments: [],
  backUp: [],
};
export default function rootReducer(state = initialState, action) {
  switch (action.type) {
    case GET_ALL_DOGS:
      return { ...state, dogs: action.payload, backUp: action.payload };
    case GET_DOG_BY_ID:
      return { ...state, dogDetail: action.payload };
    case CREATE_DOG:
      return "dog created";
