import {
  GET_ALL_DOGS,
  GET_DOG_BY_ID,
  CREATE_DOG,
  GET_DOG_BY_NAME,
  GET_TEMPERAMENTS,
  FILTER_BY_TEMPERS,
} from "../actions/actions";
const initialState = {
  dogs: [],
  dogDetail: {},
  temperaments: [],
  backUp: [],
};
export default function rootReducer(state = initialState, action) {
  switch (action.type) {
    case GET_ALL_DOGS:
      return { ...state, dogs: action.payload, backUp: action.payload };
    case GET_DOG_BY_ID:
      return { ...state, dogDetail: action.payload };
    case CREATE_DOG:
      return "dog created";
    case GET_DOG_BY_NAME:
      return { ...state, dogs: action.payload };
    case GET_TEMPERAMENTS:
      return { ...state, temperaments: action.payload };
    case FILTER_BY_TEMPERS:
      for (let i = 0; i < action.payload.length; i++) {
        let filteredDogs = state.backUp.filter(e =>{
            e.temperament.includes(action.payload[i])
        })
        // console.log(action.payload[i])
        // console.log(typeof action.payload[i])
      }

      return { ...state, dogs : filteredDogs };

    default:
      return state;
  }
}
