const initialState ={
    dogs:[],
    dogDetail:{},
    temperaments:[],
    filtered : []
}
export default function rootReducer(state = initialState,action){
switch(action.type){
    case GET_ALL_DOGS:
        return {...state,dogs:action.payload,filtered:action.payload}
        case GET_DOG_BY_ID:
            return {...state,dogDetail:action.payload}
        case CREATE_DOG:
