import {GET_ALL_DOGS,GET_DOG_BY_ID,CREATE_DOG,GET_DOG_BY_NAME,GET_TEMPERAMENTS,FILTER_BY_TEMPERS} from '../actions/actions'
const initialState ={
    dogs:[],
    dogDetail:{},
    temperaments:[]
}
export default function rootReducer(state = initialState,action){
switch(action.type){
    case GET_ALL_DOGS:
        return {...state,dogs:action.payload}
        case GET_DOG_BY_ID:
            return {...state,dogDetail:action.payload}
        case CREATE_DOG:
            return 'dog created'
            case GET_DOG_BY_NAME:
                return {...state,dogs:action.payload}
        case GET_TEMPERAMENTS:
            return {...state,temperaments:action.payload}
        case FILTER_BY_TEMPERS:
            if(state.dogs.length>0){
                let filteredDogs=[]
                for(let i = 0;i< action.payload.length;i++){

                    state.dogs.forEach(dog=>{
                        if(dog.temperament.includes(action.payload[i])){
                        if(!filteredDogs.includes(dog)){
                            filteredDogs.push(dog);
                        }
                        
                        }
                    })
                }
                
            return {...state,dogs: filteredDogs}
            }
            
        default: return state;
}
}
