import {GET_ALL_DOGS} from '../actions/actions'
const initialState ={
    dogs:[]
    // temperaments?
}
export default function rootReducer(state = initialState,action){
switch(action.type){
    case GET_ALL_DOGS:
        return {...state,dogs:action.payload}
}
}
